mapper.select:
select count(*) + (select count(*) from m_user where id = #{searchID})
from fm_user
where id = #{searchID}

select count(*) + (select count(*) from m_user where id = #{id} and pw = #{pw})
from fm_user
where id = #{id}
  and pw = #{pw}

select count(*)
from m_user
where id = #{id}

select count(*)
from fm_user
where id = #{id}

mapper.update:
update ${tableName} as m
set m.match = true
where id = #{id}

mapper.select:
select count(*)
from ${tableName}
where id = #{id}
  and `match` = true
